mapper.select:
SELECT userNo 
FROM user 
WHERE loginId = #{loginId}

SELECT userNo, loginId, name, birth, mobile, joinDtm, updateDtm
FROM user 
WHERE loginId = #{loginId}

SELECT password
FROM user 
WHERE loginId = #{loginId}

SELECT loginId
FROM user 
WHERE loginId = #{loginId}

SELECT loginId
FROM user 
WHERE name = #{name} 
AND birth = #{birth} 
AND mobile = #{mobile}

SELECT 
    CASE
        WHEN EXISTS (
            SELECT 1
            FROM user
            WHERE loginId = #{loginId} 
            AND name = #{name} 
            AND mobile = #{mobile}
        ) THEN 1
        ELSE 0
    END AS is_exists

mapper.update:
UPDATE user 
SET password = #{password} 
WHERE loginId = #{loginId} 
	AND name = #{name} 
	AND mobile = #{mobile}
